INSERT INTO dunes VALUES (600,'PENDING','Операция    не завершена.','An operation is pending','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (601,'ERROR_INVALID_PORT_HANDLE','Обнаружен неверный индекс порта.','The port handle is invalid','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (602,'ERROR_PORT_ALREADY_OPEN','Указанный    порт уже открыт.','The port is already open','COM-порт, который должен быть использован    сетевым соединением уже используется другим    другим активным сетевым соединением или    процессом (например, программой мониторинга    телефонной линии, такой как факс-программой).    Завершите работу с приложением, блокирующим    COM-порт');
INSERT INTO dunes VALUES (603,'ERROR_BUFFER_TOO_SMALL','Буфер    вызывающей программы слишком мал.','The buffer is too small.','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (604,'ERROR_WRONG_INFO_SPECIFIED','Была указана некорректная информация.','The wrong information is specified.','Возможно, файл телефонной книги    удаленного доступа и текущая конфигурация    Удаленного Доступа к Сети несовместимы. Если вы    заменили коммуникационное оборудование    (последовательный порт или модем), проверьте    конфигурацию Удаленного Доступа к Сети.');
INSERT INTO dunes VALUES (605,'ERROR_CANNOT_SET_PORT_INFO','Информация порта не может быть    установлена.','Cannot set port information.','Возможно, файл телефонной книги    удаленного доступа и текущая конфигурация    Удаленного Доступа к Сети несовместимы. Если вы    заменили коммуникационное оборудование    (последовательный порт или модем), проверьте    конфигурацию Удаленного Доступа к Сети.<br>    Если эта ошибка будет возникать в дальнейшем,    удалите и создайте заново удаленное соединение к    сети.');
INSERT INTO dunes VALUES (606,'ERROR_PORT_NOT_CONNECTED','Указанный    порт не подключен.','The port is not connected','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (607,'ERROR_EVENT_INVALID','Обнаружено    неверное событие.','The event is invalid.','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (608,'ERROR_DEVICE_DOES_NOT_EXIST','Указанное устройство не существует.','The device does not exist.','Возможно, файл телефонной книги    удаленного доступа и текущая конфигурация    Удаленного Доступа к Сети несовместимы. Если вы    заменили коммуникационное оборудование    (последовательный порт или модем), проверьте    конфигурацию Удаленного Доступа к Сети.<br>    Если эта ошибка будет возникать в дальнейшем,    удалите и создайте заново удаленное соединение к    сети.');
INSERT INTO dunes VALUES (609,'ERROR_DEVICETYPE_DOES_NOT_EXIS','Указанный тип устройства не    существует.','The device type does not exist.','Возможно, файл телефонной книги    удаленного доступа и текущая конфигурация    Удаленного Доступа к Сети несовместимы. Если вы    заменили коммуникационное оборудование    (последовательный порт или модем), проверьте    конфигурацию Удаленного Доступа к Сети.<br>    Если эта ошибка будет возникать в дальнейшем,    удалите и создайте заново удаленное соединение к    сети.');
INSERT INTO dunes VALUES (610,'ERROR_BUFFER_INVALID','Был    указан неверный буфер.','The buffer is invalid.','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (611,'ERROR_ROUTE_NOT_AVAILABLE','Указанный маршрут недоступен.','The route is not available.','Вероятно, конфигурация сети неверна.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (612,'ERROR_ROUTE_NOT_ALLOCATED','Указанный маршрут не локализован.','The route is not allocated.','Вероятно, конфигурация сети неверна.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу. Эта ошибка также может    произойти, если ваш компьютеру не хватает    рабочих ресурсов.');
INSERT INTO dunes VALUES (613,'ERROR_INVALID_COMPRESSION SPEC','Указано неверное сжатие.','Invalid compression is specified.','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (614,'ERROR_OUT_OF_BUFFERS','Доступный буфер недостаточен.','You are out of buffers.','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (615,'ERROR_PORT_NOT_FOUND','Указанный порт не найден.','The port is not found.','Возможно,    файл телефонной книги удаленного доступа и    текущая конфигурация Удаленного Доступа к Сети    несовместимы. Если вы заменили коммуникационное    оборудование (последовательный порт или модем),    проверьте конфигурацию Удаленного Доступа к    Сети.<br>    Если эта ошибка будет возникать в дальнейшем,    удалите и создайте заново удаленное соединение к    сети.');
INSERT INTO dunes VALUES (616,'ERROR_ASYNC_REQUEST_PENDING','Асинхронный запрос не завершен.','An asynchronous request is pending.','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (617,'ERROR_ALREADY_DISCONNECTING','Модем уже отключен.','The port or device is already disconnecting.','Дождитесь, пока Удаленный Доступ к Сети    завершит соединение');
INSERT INTO dunes VALUES (618,'ERROR_PORT_NOT_OPEN','Указанный    порт не открыт.','The port is not open.','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (619,'ERROR_PORT_DISCONNECTED','Указанный    порт не подключен.','The port is disconnected.','Перегрузите компьютер для того члобы    убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (620,'ERROR_NO_ENDPOINTS','Конечная    точка не может быть определена.','There are no endpoints.','Вероятно,    конфигурация сети неверна. Перегрузите    компьютер для того члобы убедиться, что все    недавние изменения конфигурации вступили в    силу.');
INSERT INTO dunes VALUES (621,'ERROR_CANNOT_OPEN_PHONEBOOK','Система не смогла открыть телефонную    книгу.','Cannot open the phone book file.','Удаленный Доступ к Сети использует файл    Rasphone.pbk, находящийся в директории System32Ras.    Убедитесь, что файл находится в этой директории,    после чего перезапустите Удаленный Доступ к    Сети');
INSERT INTO dunes VALUES (622,'ERROR_CANNOT_LOAD_PHONEBOOK','Система не смогла загрузить телефонную    книгу.','Cannot load the phone book file.','Удаленный Доступ к Сети использует файл    Rasphone.pbk, находящийся в директории System32Ras.    Убедитесь, что файл находится в этой директории,    после чего перезапустите Удаленный Доступ к    Сети');
INSERT INTO dunes VALUES (623,'ERROR_CANNOT_FIND_PHONEBOOK_EN','Система не смогла найти запись в    телефонной книге для данного подключения.','Cannot find the phone book entry.','Удаленный Доступ к Сети нашел    телефонную книгу, но не может найти запись,    соответствующую данному подключению. Эта ошибка    не будет происходить если только другое    приложение не использует телефонную книгу    Удаленного Доступа к Сети и не укажет неверную    запись подключения.');
INSERT INTO dunes VALUES (624,'ERROR_CANNOT_WRITE_PHONEBOOK','Система не смогла обновить файл    телефонной книги.','Cannot write to the phone book file','Удаленный Доступ к Сети использует файл    Rasphone.pbk, находящийся в директории System32Ras.    Убедитесь, что ваш диск не переполнен и у вас есть    права для изменения этого файла.).');
INSERT INTO dunes VALUES (625,'ERROR_CORRUPT_PHONEBOOK','Система    нашла в телефонной книге неверную информацию.','Invalid information is found in the phone book file.','Файл телефонной книги Rasphone.pbk может быть    поврежден. Удалите этот файл из директории    System32Ras, после чего перезапустите Удаленный    Доступ к Сети для того члобы создать новую    телефонную книгу.');
INSERT INTO dunes VALUES (626,'ERROR_CANNOT_LOAD_STRING','Строка    не может быть загружена.','Cannot load a string.','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (627,'ERROR_KEY_NOT_FOUND','Ключ    не может быть найден.','Cannot find a key.','Возможно,    один из конфигурационных файлов Удаленного    Доступа к Сети содержит неверную информацию.    Если вы используете модем, не поддерживаемый    вашей версией Windows, установите поддерживаемый    модем.');
INSERT INTO dunes VALUES (628,'ERROR_DISCONNECTION','Подключение    прервано.','The port is disconnected.','Если это подключение по телефонной    линии, попробуйте передозвониться. Если вы    продолжаете получать это сообщение, понизьте    начальную скорость модема и отключите    дополнительные настройки модема. Если эта    проблема будет возникать в дальнейшем,    обратитесь к вашему системному администратору.<br>    Если это подключение к виртуальной частной сети    (VPN), в доступе может быть отказано по причине    политики удаленного доступа, либо по иным    причинам. Обратитесь к вашему системному    администратору');
INSERT INTO dunes VALUES (629,'ERROR_REMOTE_DISCONNECTION','Подключение прервано удаленным    компьютером.','The port is disconnected from the remote machine.','Соединение разорвано    по одной из следующих причин:<br>    <ul>      <li>Неисправимая ошибка телефонной        линии. </li>      <li>Шумы в линии. </li>      <li>Отключение системным        администратором. </li>      <li>Неудавшаяся попытка соединения с        модемом сервера удаленного доступа на выбранной        скорости. </li>    </ul>    <p>Для того чтобы заново установить    соединение, нажмите &quot;Перенабрать&quot;. Вы также    можете активировать автоматический передозвон в    разделе Свойства для данного соединения. Если    эта ошибка будет возникать в дальнейшем,    понизьте скорость подключения модема до 9600 bps,    после чего попробуйте передозвониться.<br>    Вы можете попробовать подключиться к другому    серверу, для того чтобы определить, не связана ли    эта проблема конкретно с севером, на который вы    пытаетесь дозвониться. Также, вы может    попробовать подключиться к своему серверу с    другой телефонной линии.)(После установки Windows 98    Second Edition перестанет работать 128-битное шифрование    данных, и при попытке установить соединение    удаленного доступа, использующее 128-битное    шифрование, будет появляться следующее    сообщение об ошибке: >Error 629: You have    been disconnected from the computer you dialed. Double-click the connection to try again.');
INSERT INTO dunes VALUES (630,'ERROR_HARDWARE_FAILURE','Модем    отключен из-за аппаратной неисправности.','The port is disconnected due to a hardware    failure.The computer is not receiving a response from the modem. Check that the modem is    plugged in, and if necessary, turn the modem off, and then tur','Соединение    разорвано по одной из следующих причин: <ul>      <li>В вашем модеме (или в другом        коммуникационном оборудовании) произошла        неисправимая ошибка. </li>      <li>В вашем коммуникационном порту        произошла неисправимая ошибка. </li>      <li>Кабель вашего модема отключен. </li>    </ul>    <p>Для диагностики и решения проблемы    проделайте следующее: <ul>      <li>Убедитесь, что ваш модем включен и        кабель надежно подключен. </li>      <li>Убедитесь, что ваш модем        функционирует правильно.)(Эта ошибка может        возникать в тех случаях, когда модем пытается        использовать только что найденный COM-порт,        установленный при загрузке Windows 98. Для устранения        ошибки измените соответствующие свойства        соединения удаленного доступа. Ошибка может        также возникать по вине некоторых программ,        автоматически запускаемых при загрузке системы.');
INSERT INTO dunes VALUES (631,'ERROR_USER_DISCONNECTION','Пользователь    отключил модем.','The port is disconnected by the user.','Из-за события, произошедшего на вашем    компьютере соединение было разорвано.    Перезвоните.');
INSERT INTO dunes VALUES (632,'ERROR_INVALID_SIZE','Определен    неверный размер структуры.','The structure size is incorrect.','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (633,'ERROR_PORT_NOT_AVAILABLE','Модем уже используется или не    сконфигурирован для исходящих звонков.','The port is already in use or is not configured for    Remote Access dial out.The modem is not installed or configured for Dial-Up Networking. To    check your modem configuration, double click the Mo','Если это подключение по телефонной    линии, COM-порт, который должен быть использован    сетевым соединением уже используется другим    другим активным сетевым соединением или    процессом (например, программой мониторинга    телефонной линии, такой как факс-программой).    Завершите работу с приложением, блокирующим    COM-порт.<br>    Если это подключение к виртуальной частной сети    (VPN), Устройство VPN, которое должно быть    использовано сетью, не может быть открыто. Если    эта проблема будет возникать в дальнейшем,    обратитесь к вашему системному    администратору)(Такая ошибка может возникать в    тех случаях, когда отсутствует или поврежден    файл Telephon.ini, а также при некорректном значении в    ветке TAPI в реестре. Для восстановления файла    Telephon.ini проделайте следующее:<br>    0. Откройте папку Windows и найдите файл Telephon.ini <br>    1. Если такой файл имеется, переименуйте его в    Telephon.old <br>    2. Запустите программу tapiini.exe (Start - Run - tapiini.exe)<br>    3. Перезагрузите компьютер <br>    Для корректировки реестра проделайте следующее:<br>    4. Запустите редактор реестра (regedit.exe) <br>    5. Откройте ветку HKEY_LOCAL_MACHINESoftwareMicrosoft WindowsCurrent    Version TelephonyProviders <br>    6. Двойным щелчком откройте ключ ProviderFilename0 и    измените его значение на &quot;TSP3216L.TSP&quot; (без    кавычек)<br>    7. Закройте редактор реестра и перезагрузите    компьютер.');
INSERT INTO dunes VALUES (634,'ERROR_CANNOT_PROJECT_CLIENT','Ваш компьютер не может быть    зарегистрирован в удаленной сети.','Cannot register your computer on the remote network.','Сервер удаленного доступа не может    зарегистрировать имя вашего компьютера в сети.    Чаще такое возможно с протоколом NetBIOS, однако,    также может происходить с TCP/IP или IPX протоколами.    Обычно, это происходит в случае, если адрес уже    используется в сети. Обратитесь к вашему    системному администратору.');
INSERT INTO dunes VALUES (635,'ERROR_UNKNOWN','Неизвестная    ошибка.','There is an unknown error.','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (636,'ERROR_WRONG_DEVICE_ATTACHED','Устройство, подключенное к порту,    отличается от ожидаемого.','The wrong device is attached to the port.','Возможно, ваша аппаратная конфигурация    и настройки конфигурации для вашего подключения    несовместимы друг с другом. Если вы заменили    коммуникационное оборудование    (последовательный порт или модем), проверьте    конфигурацию Удаленного Доступа к Сети.');
INSERT INTO dunes VALUES (637,'ERROR_BAD_STRING','Обнаруженная    строка не может быть конвертирована.','The string could not be converted.','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (638,'ERROR_REQUEST_TIMEOUT','Истекло    время запроса.','The request has timed out.','Произошла внутренняя ошибка.    Перегрузите компьютер для того члобы убедиться,    что все недавние изменения конфигурации    вступили в силу.');
INSERT INTO dunes VALUES (639,'ERROR_CANNOT_GET_LANA','Асинхронная    сеть недоступна.','There is no asynchronous net available.','Возможно, ваша сеть NetBIOS неверно    сконфигурирована. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.');
INSERT INTO dunes VALUES (640,'ERROR_NETBIOS_ERROR','Произошла    ошибка в NetBIOS.','A NetBIOS error has occurred.','Модем не может установить соединение на    указанной скорости. Понизьте начальную скорость    модема и перезвоните. Также, попробуйте    отключить аппаратное и программное сжатие.<br>    Если установить соединение все-таки не удается,    попробуйте добавить NWLink IPX/SPX/NetBIOS Compatible Transport    Protocol.');
INSERT INTO dunes VALUES (641,'ERROR_SERVER_OUT_OF_RESOURCES','Сервер не может определить место    ресурсов NetBIOS, необходимых для поддержки    клиента.','The server cannot allocate NetBIOS resources needed    to support the client.','Попросите вашего    системного администратора увеличить емкость    ресурса сервера удаленного доступа, или    используйте Services in Computer Management для остановки    второстепенных сервисов на вашем компьютере,    таких как Messenger и Network DDE.');
INSERT INTO dunes VALUES (642,'ERROR_NAME_EXISTS_ON_NET','Одно    из имен NetBIOS вашего компьютера уже    зарегистрировано в удаленной сети.','One of your NetBIOS names is already registered on    the remote network.','Другой    компьютер с таким же именем уже подключен к    удаленной сети. Каждый компьютер в этой сети    должен быть зарегистрирован бод уникальным    именем. Проверьте следующее:<br>    <ul>      <li>Не находится ли компьютер с каким же        именем как ваш в сети к которой вы подключаетесь. </li>      <li>Не подключен ли физически ваш        компьютер к сети, к которой вы пытаетесь        подключиться. ) </li>    </ul>    \n');
INSERT INTO dunes VALUES (643,'ERROR_SERVER_GENERAL_NET_FAILU','Сетевой адаптер сервера неисправен.','A network adapter at the server is not working.','Сообщите об ошибке вашему системному    администратору.');
INSERT INTO dunes VALUES (644,'WARNING_MSG_ALIAS_NOT_ADDED','Вы не будете получать сетевые    сообщения.','You do not receive network message pop-ups.','Другой компьютер, уже подключенный к    этой сети использует ваше имя для получения    сетевых сообщений. Адресуемые вам сообщения    отправляются на этот компьютер. Если вы хотите    получать сообщения на вашу удаленную рабочую    станцию, вам следует выйти из вашего офисного    компьютера до того как вы будете дозваниваться в    сеть с следующий раз.<br>    Эта ошибка не влияет на сообщения Microsoft Outlook, Microsoft    Outlook Express, или Microsoft Exchange.');
INSERT INTO dunes VALUES (645,'ERROR_AUTH_INTERNAL','Внутренняя    ошибка аутентификации.','There is an internal authentication error.Dial-Up    Networking could not complete the connection to the server.','Произошла    внутренняя ошибка. Перегрузите компьютер для    того члобы убедиться, что все недавние изменения    конфигурации вступили в силу.)<br>    (Обычно подобное сообщение вызвано тем, что в    настройках соединения на закладке Server Types    установлена опция Require encrypted password. Отключите ее.');
INSERT INTO dunes VALUES (646,'ERROR_RESTRICTED_LOGON_HOURS','Запрещен доступ в это время суток.','The account is not permitted to logon at this time    of day.','Ваша учетная запись    сконфигурирована для доступа в сеть,    ограниченного во времени суток. Если вам    необходимо войти в сеть во время суток, отличное    от сконфигурированного на данный момент,    попросите вашего системного администратора    изменить конфигурацию.');
INSERT INTO dunes VALUES (647,'ERROR_ACCT_DISABLED','Пользователь    блокирован.','The account is disabled.','Учетная    запись пользователя отключена. Это могло    произойти из-за множественных повторяющихся    неудачных попыток подключения, либо потому что    системный администратор отключил учетную запись    из соображений безопастности (или за неуплату).    Попросите вашего системого администратора    включить вашу учетную запись.');
INSERT INTO dunes VALUES (648,'ERROR_PASSWD_EXPIRED','Срок    действия пароля этого пользователя истек.','The password has expired.','Необходимо изменить пароль.<br>    Если вы подключаетесь через Удаленный Доступ к    Сети, вы автоматически получите запрос на    изменение вашего пароля. Если вы подключаетесь,    используя команду rasdial, вы можете изменить пароль    проделав следующее:<br>    <ul>      <li>Нажмите CTRL+ALT+DEL. К </li>      <li>ликните &quot;Изменить пароль&quot;,        после чего следуйте инструкциям, появляющимся на        экране. </li>    </ul>    <p>Если вы принадлежите к группе    администраторов и срок действия вашего пароля    истек, другой администратор должен изменить ваш    пароль. Вы не сможете изменить ваш пароль    самостоятельно.');
INSERT INTO dunes VALUES (649,'ERROR_NO_DIALIN_PERMISSION','Пользователь не имеет прав доступа.','The account does not have Remote Access permission.','Учетная запись не имеет прав доступа на    сервер по одной из следующих причин:<br>    <ul>      <li>Ваша учетная запись на выбранном        сервере действительна, но у вас нет прав        удаленного доступа к сети. Попросите вашего        системного администратора установить        разрешение на удаленный доступ для вашей учетной        записи, либо установить разрешение в Роутинге и        Удаленном Доступе. </li>      <li>Срок действия вашей учетной записи        истек и учетная запись была отключена, либо ваш        удаленный доступ был закрыт. </li>      <li>Вы пытаетесь подключиться к серверу        вне разрешенного времени суток, количество        попыток подключения превысило максимально        допустимое число, либо ваша учетная запись может        не позволять удаленный вход. </li>      <li>Параметры вашей учетной записи        могут предотвращать подключения. Напимер, ваша        учетная запись может разрешать подключения        только с определенных телефонных номеров.        Удаленный компьютер может разрешать подключения        только локальным учетным записям. </li>      <li>Возможно, ваш компьютер не        поддерживает запрашиваемый протокол        аутентификации, либо ваш компьютер пытается        использовать протокол, не допустимый удаленным        компьютером. </li>    </ul>    <p>Если у вас есть учетная запись с    провами удаленного доступа на другом домене,    проделайте следующее, для использования вашей    учетной записи на этом домене:<br>    <ol>      <li>Кликните правой кнопкой по        подключению, после чего выберите Свойства. </li>      <li>На закладке Свойства выберите        Запрашивать имя и пароль, сертификат и т.д. и        Включать Windows logon domain чекбоксы. </li>      <li>На закладке Безопасность очистите        чекбоксы Автоматически использовать мои логин,        пароль и домен, затем кликните ОК. </li>      <li>Кликните дважды по подключению,        затем кликните Набрать. </li>      <li>Укажите соответствующие имя        пользователя, пароль и домен. ');
INSERT INTO dunes VALUES (650,'ERROR_SERVER_NOT_RESPONDING','Сервер удаленного доступа не отвечает.','The Remote Access server is not responding.Modem    not responding (Error 650). For troubleshooting information,click Help.','Эту проблему можно обойти, включив    запуск окна терминала:<br>    0. Откройте папку удаленного доступа <br>    1. Правой кнопкой щелкните по выбранному    соединению и выберите Properties в открывшемся меню <br>    2. На закладке General нажмите кнопку Configure <br>    3. Перейдите на закладку Options <br>    4. Поставьте галочку в пункте Bring up terminal window after    dialing <br>    Теперь после установки соединения будет    появляться окно теминала. Для его закрытия    просто нажимайте Continue.)<br>    <br>    (Еще одна ошибка 650:<br>    (650) The computer you have dialed does not respond to a network request <br>    Как правило, такая ошибка появляется в тех    случаях, когда ваш провайдер перегружен и не    может выделить вам свободный IP-адрес.');
INSERT INTO dunes VALUES (651,'ERROR_FROM_DEVICE','Модем    сообщил об ошибке.','Your modem (or other connecting device) has reported    an error.','Ваш модем (либо другое    устройство) сообщило об ошибке.<br>    Если вы подключаетесь по телефонной линии и    используете поддерживаемый внешний модем,    выключите и затем включите модем. Закройте и    снова запустите Удаленный Доступ к Сети, после    чего перезвоните. Если ваш модем не    поддерживается Удаленным Доступом к Сети,    переключитесь на поддерживаемый модем. Самый    современный список поддерживаемых модемов    находится на сайте Microsoft <a href=\"http://www.microsoft.com/\">www.microsoft.com</a>.    Убедитесь, что вы верно сконфигурировали ваш    модем для работы с удаленным доступом.<br>    Если это подключение к виртуальной частной сети    (VPN), вы, возможно, неверно указали адрес TCP/IP в    конфигурации сети, либо сервер, к которому вы    пытаетесь подключиться, может быть недоступен.    Для того чтобы определить, свободен ли сервер,    обратитесь к вашему системному администратору');
INSERT INTO dunes VALUES (652,'ERROR_UNRECOGNIZED_RESPONSE','Получен нераспознанный ответ модема.','There is an unrecognized response from the device.','Ваш модем (либо другое устройство) выдал    сообщение, отсутствующее в одном из ваших    скриптовых файлов (Pad.inf, Switch.inf или filename.scp).<br>    Если вы используете поддерживаемый внешний    модем, выключите и затем включите модем, после    чего перезвоните.<br>    Если эта проблема будет возникать в дальнейшем,    попробуйте подключиться на меньшей начальной    скорости.<br>    Самый современный список поддерживаемых модемов    находится на сайте Microsoft <a href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (653,'ERROR_MACRO_NOT_FOUND','Запрашиваемый    модемом макрос не найден в разделе .INF-файла    устройства.','A macro required by the device is not found in the    &lt;device&gt;.INF file section.','Возможно, один    из конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (654,'ERROR_MACRO_NOT_DEFINED','Команда    или ответ в разделе .INF-файла устройства    обращается к неопределенному макросу.','A command or response in the &lt;device&gt;.INF file    section refers to an undefined macro.','Возможно,    один из конфигурационных файлов Удаленного    Доступа к Сети содержит недействительную    информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (655,'ERROR_MESSAGE_MACRO_NOT_FOUND','&lt;MESSAGE&gt;-макрос не найден в разделе    .INF-файла устройства.','The Message macro is not found in the    &lt;device&gt;.INF file section.','Возможно, один    из конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (656,'ERROR_DEFAULTOFF_MACRO_NOT_FOU','&lt;DEFAULTOFF&gt;-макрос в разделе .INF-файла    устройства содержит неопределенный макрос.','The Defaultoff macro in the &lt;device&gt;.INF file    section contains an undefined macro.','Возможно,    один из конфигурационных файлов Удаленного    Доступа к Сети содержит недействительную    информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (657,'ERROR_FILE_COULD_NOT_BE_OPENED','INF-файл не может быть открыт.','The &lt;device&gt;.INF file could not be opened.','Возможно, один из конфигурационных    файлов Удаленного Доступа к Сети содержит    недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (658,'ERROR_DEVICENAME_TOO_LONG','Слишком длинное имя устройства в    .INF-файле устройства или .INI-файле медиа.','The device name in the &lt;device&gt;.INF or the    &lt;media&gt;.INI file is too long.','Возможно,    один из конфигурационных файлов Удаленного    Доступа к Сети содержит недействительную    информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (659,'ERROR_DEVICENAME_NOT_FOUND','.INI-файл медиа обращается к неизвестному    имени устройства.','The &lt;media&gt;.INI file refers to an unknown    device name.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (660,'ERROR_NO_RESPONSES','.INF-файл    устройства не содержит ответов для команд.','The &lt;device&gt;.INF file contains no responses    for the command.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (661,'ERROR_NO_COMMAND_FOUND','В    .INF-файле устройства отсутствует команда.','The &lt;device&gt;.INF file is missing a command.','Возможно, один из конфигурационных    файлов Удаленного Доступа к Сети содержит    недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (662,'ERROR_WRONG_KEY_SPECIFIED','Произошла попытка установить макрос, не    указанный в разделе .INF-файла устройства.','The macro you are using is not listed in the    &lt;device&gt;.INF file.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (663,'ERROR_UNKNOWN_DEVICE_TYPE','.INI-файл медиа обращается к устройству    неизвестного типа.','The &lt;media&gt;.INI file refers to an unknown    device type.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (664,'ERROR_ALLOCATING_MEMORY','Системе    не хватает памяти.','Cannot allocate memory.','Системе    не хватает памяти. Закройте некоторые программы,    после чего перезвоните.');
INSERT INTO dunes VALUES (665,'ERROR_PORT_NOT_CONFIGURED','Модем неправильно сконфигурирован.','The port is not configured for Remote Access.','Если устройство уже используется    другим подключением, прервите то подключение.<br>    Перенастройте подключение для использования    другого устройства.');
INSERT INTO dunes VALUES (666,'ERROR_DEVICE_NOT_READY','Модем    не функционирует.','Your modem (or other connecting device) is not    functioning.','Ваш модем (либо другое    устройство) не отвечает по одной из следующих    причин:<br>    <ul>      <li>Внешний модем выключен. </li>      <li>Модем ненадежно подключен к        компьютеру. Убедитесь, что кабель надежно        подключен как к модему, так и к компьютеру. </li>      <li>Последовательный кабель не        соответствует требованиям Удаленного Доступа к        Сети. </li>      <li>Модем испытывает аппаратные        проблемы. Выключите модем, подождите 20 секунд, и        включите модем.');
INSERT INTO dunes VALUES (667,'ERROR_READING_INI_FILE','Системе    не удается прочесть .INI-файл медиа.','Cannot read the &lt;media&gt;.INI file.','Возможно, один из конфигурационных    файлов Удаленного Доступа к Сети содержит    недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (668,'ERROR_NO_CONNECTION','Соединение    было прервано.','There is no connection.','Передозвонитесь.    Если вы будете продолжать получать это    сообщение, отмените начальную скорость модема и    оключите дополнительные параметры модема, такие    как программное сжатие. Если эта проблема будет    возникать в дальнейшем, Обратитесь к вашему    системному администратору.<br>    Также, вы должны убедиться, что модем совместим.    Самый современный список поддерживаемых модемов    находится на сайте Microsoft <a href=\"http://www.microsoft.com/\">www.microsoft.com</a>.<br>    Если вы дозваниваетесь вручную, убедитесь, что вы    находитесь в состоянии подключения, когда    нажимаете Готово. Вы дозваниваетесь вручную,    если вы выбрали Набор Номера через Оператора в    меню дополнительно папки Удаленный Доступ к    Сети.');
INSERT INTO dunes VALUES (669,'ERROR_BAD_USAGE_IN_INI_FILE','Параметры использования .INI-файла медиа    недействительны.','The usage parameter in the &lt;media&gt;.INI file is    invalid.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (670,'ERROR_READING_SECTIONNAME','Системе не удается прочесть имя раздела    в .INI-файле медиа.','Cannot read the section name from the    &lt;media&gt;.INI file.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (671,'ERROR_READING_DEVICETYPE','Системе    не удается прочесть тип устройства в .INI-файле    медиа.','Cannot read the device type from the    &lt;media&gt;.INI file.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (672,'ERROR_READING_DEVICENAME','Системе    не удается прочесть имя устройства в .INI-файле    медиа.','Cannot read the device name from the    &lt;media&gt;.INI file.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (673,'ERROR_READING_USAGE','Системе    не удается прочесть частоту использования в    .INI-файле медиа.','Cannot read the usage from the &lt;media&gt;.INI    file.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (674,'ERROR_READING_MAXCONNECTBPS','Системе не удается прочесть    максимальную скорость передачи данных в .INI-файле    медиа.','Cannot read the maximum connection BPS rate from the    &lt;media&gt;.INI file.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (675,'ERROR_READING_MAXCARRIERBPS','Системе не удается прочесть    максимальную скорость подключения в .INI-файле    медиа.','Cannot read the maximum carrier BPS rate from the    &lt;media&gt;.INI file.','Возможно, один из    конфигурационных файлов Удаленного Доступа к    Сети содержит недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (676,'ERROR_LINE_BUSY','Телефонная    линия занята.','The line is busy.','Перенаберите    номер. Вы можете автоматизировать перенабор на    закладке Свойства в настройке срединения. Если    это подключение к виртуальной частной сети (VPN),    проверьте имя или IP адрес сервера, к которому вы    подключаетесь и попробуйте подключиться снова.    Кроме того, обратитесь к вашему системному    администратору для того чтобы проверить,    работает ли удаленный сервер.');
INSERT INTO dunes VALUES (677,'ERROR_VOICE_ANSWER','Вместо    модема ответил человек.','A voice line is answering instead of a modem.','Модем или другое устройство не снимает    телефонную трубку. Проверьте номер и    перенаберите его снова. Если это подключение к    виртуальной частной сети (VPN), проверьте имя или IP    адрес сервера, к которому вы подключаетесь и    попробуйте подключиться снова.');
INSERT INTO dunes VALUES (678,'ERROR_NO_ANSWER','Отсутствует    ответ.','There is no answer.','Модем    или другое устройство не снимает телефонную    трубку. Проверьте номер и перенаберите его снова.    Если это подключение к виртуальной частной сети    (VPN), проверьте имя или IP адрес сервера, к которому    вы подключаетесь и попробуйте подключиться    снова.<br>    Также, убедитесь, что телефонная линия    подключена в правильное гнездо модема.');
INSERT INTO dunes VALUES (679,'ERROR_NO_CARRIER','Система    не смогла определить несущую.','Cannot detect carrier.','Модем    или другое устройство не снимает телефонную    трубку. Многие модемы выдают эту ошибку, если    удаленный модем не снимает трубку. Проверьте    номер и перенаберите его снова. Если это    подключение к виртуальной частной сети (VPN),    проверьте имя или IP адрес сервера, к которому вы    подключаетесь и попробуйте подключиться снова.    Вы можете автоматизировать перенабор на    закладке Свойства в настройке срединения.');
INSERT INTO dunes VALUES (680,'ERROR_NO_DIALTONE','Отсутствует    гудок в линии.','There is no dial tone.','Телефонная    линия может быть неверно подключена к вашему    модему, либо отключена от него. Возможно, перед    номером необходимо установить префикс, например    9, для выхода на внешнюю линию, либо номер мог    оказаться слишком длинным.<br>    Убедитесь, что телефонная линия подключена в    правильное гнездо модема. Также, убедитесь, что    для подключения к внешней линии вы указали    специальные номера доступа, например префикс 9 с    последующей запятой.<br>    Проверьте, отсутствует ли на вашей телефонной    линии прерывистый тональный сигнал,    соответствующий сообщению автоответчика.<br>    Многие модемы не могут набрать более 34 цифр.    После введения номера, превышающего эту длинну,    эти модемы разбивают его на две, либо более строк,    из которых набирают только первую    (незаконченную). Это происходит модемами USRobotics и    Multitech. Единственным известным решением этой    проблемы является использование модемов других    производителей.<br>    Дополнительную информацию можно получить в Personal    Support Center на сайте Microsoft <a href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
INSERT INTO dunes VALUES (681,'ERROR_IN_COMMAND','Модем    сообщает об общей ошибке.','There is a general error reported by a device.','Возможно, один из конфигурационных    файлов Удаленного Доступа к Сети содержит    недействительную информацию.<br>    Если ваш модем не поддерживается Удаленным    Доступом к Сети, переключитесь на поддерживаемый    модем. Самый современный список поддерживаемых    модемов находится на сайте Microsoft <a    href=\"http://www.microsoft.com/\">www.microsoft.com</a>.');
